import { Component } from '@angular/core';

@Component({
  selector: 'nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {

scrollToElement(elementId: string) {
  const element = document.getElementById(elementId);
  console.log(element?.getBoundingClientRect())
  if (element) {
    element.scrollIntoView({ behavior: 'smooth' });
  }
}


}
<nav class="navbar">
    <div class="container">
        <ul class="nav">
            <li>
                <button (click)="scrollToElement('skills')" class="button"> Skills </button>
            </li>
            <li>
                <button (click)="scrollToElement('portfolio')" class="button"> Portfolio </button>

            </li>
            <li>
                <button (click)="scrollToElement('contact')" class="button"> Contact me </button>

            </li>
            <li>
                <button (click)="scrollToElement('resume')" class="button"> Resume </button>

            </li>
        </ul>    
    </div>
</nav>
